// @ts-nocheck
import { serve } from "https://deno.land/std@0.168.0/http/server.ts"
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.38.4"
import { createClient as createGeminiClient } from "generative-ai"

const corsHeaders = {
    "Access-Control-Allow-Origin": "*",
    "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type, prefer",
    "Access-Control-Allow-Methods": "POST, OPTIONS",
    "Access-Control-Max-Age": "86400",
}

serve(async (req) => {
    // Handle CORS preflight requests
    if (req.method === "OPTIONS") {
        return new Response("ok", { 
            headers: corsHeaders,
            status: 200 
        })
    }

    try {
        const supabase = createClient(
            Deno.env.get("SUPABASE_URL") ?? "",
            Deno.env.get("SUPABASE_ANON_KEY") ?? ""
        )

        // 1. Authenticate user
        const authHeader = req.headers.get("Authorization")
        if (!authHeader) throw new Error("Missing Authorization header");

        const { data: { user }, error: authError } = await supabase.auth.getUser(authHeader.replace("Bearer ", ""))
        if (authError || !user) throw new Error("Unauthorized");

        const { action, payload } = await req.json()
        const apiKey = Deno.env.get("GEMINI_API_KEY")

        if (!apiKey) {
            throw new Error("Missing GEMINI_API_KEY environment variable")
        }

        console.log(`?? Edge Function Action: ${action} for user: ${user.email}`);

        // Initialize the new Google Gen AI SDK
        const client = createGeminiClient({
            apiKey: apiKey,
        });

        if (action === "chat") {
            const { messages, systemPrompt } = payload;
            console.log("?? Chat Request:", messages[messages.length - 1].content);

            const history = messages.slice(0, -1).map((m) => ({
                role: m.role === "assistant" ? "model" : "user",
                parts: [{ text: m.content }]
            }));

            const chat = client.models.chat({
                model: "gemini-3-flash-preview",
                systemInstruction: systemPrompt,
                history: history
            });

            const result = await chat.sendMessage(messages[messages.length - 1].content);
            const text = result.candidates?.[0]?.content?.parts?.[0]?.text || "Sparky is speechlessly magical right now!";
            console.log("? Chat Response Success");

            return new Response(JSON.stringify({ text }), {
                headers: { ...corsHeaders, "Content-Type": "application/json" },
            })
        }

        if (action === "generate-story") {
            const { systemPrompt, userPrompt } = payload;
            console.log("?? Story Generation Request:", userPrompt);

            const result = await client.models.generateContent({
                model: "gemini-3-flash-preview",
                systemInstruction: systemPrompt,
                contents: [
                    { role: "user", parts: [{ text: userPrompt }] }
                ],
                generationConfig: {
                    responseMimeType: "application/json"
                }
            });

            const text = result.candidates?.[0]?.content?.parts?.[0]?.text || "{}";
            console.log("? Story Structure Success");

            return new Response(JSON.stringify({ text }), {
                headers: { ...corsHeaders, "Content-Type": "application/json" },
            })
        }

        if (action === "generate-image") {
            const { prompts, model, base64Image, mimeType } = payload;
            const targetModel = model || "gemini-2.5-flash-image"; 
            console.log(`?? Image Generation Request (${targetModel}):`, Array.isArray(prompts) ? prompts[0] : prompts);

            let parts = [];
            
            if (base64Image) {
                const base64Data = base64Image.includes(",") ? base64Image.split(",")[1] : base64Image;
                parts.push({
                    inlineData: {
                        data: base64Data,
                        mimeType: mimeType || "image/png"
                    }
                });
            }

            if (Array.isArray(prompts)) {
                prompts.forEach(p => parts.push({ text: p }));
            } else {
                parts.push({ text: prompts });
            }

            const result = await client.models.generateContent({
                model: targetModel,
                contents: [{ parts }]
            });

            let generatedImageBase64 = null;
            let responseText = "";

            if (result.candidates && result.candidates[0].content && result.candidates[0].content.parts) {
                for (const part of result.candidates[0].content.parts) {
                    if (part.inlineData) {
                        generatedImageBase64 = part.inlineData.data;
                    } else if (part.text) {
                        responseText = part.text;
                    }
                }
            }

            console.log("? Image Generation Success");

            return new Response(JSON.stringify({
                text: responseText,
                image: generatedImageBase64
            }), {
                headers: { ...corsHeaders, "Content-Type": "application/json" },
            })
        }

        throw new Error("Invalid action")

    } catch (error) {
        console.error("? Edge Function Final Catch:", error);

        let statusCode = 500;
        let userMessage = "An unexpected error occurred. Please try again later.";

        const errorMessage = error instanceof Error ? error.message : String(error);
        const errorString = JSON.stringify(error);

        if (errorString.includes("429") || errorString.includes("quota") || errorMessage.includes("quota") || errorMessage.includes("429")) {
            statusCode = 429;
            userMessage = "?? Our magic is taking a quick break! We hit our daily magic limit. Please try again in a few hours!";
        }

        return new Response(JSON.stringify({
            error: userMessage,
            details: errorMessage,
            code: statusCode
        }), {
            headers: { ...corsHeaders, "Content-Type": "application/json" },
            status: 200,
        })
    }
})
