import { serve } from "https://deno.land/std@0.168.0/http/server.ts"
import { createClient } from "https://esm.sh/@supabase/supabase-js@2"
import { createClient as createGeminiClient } from "npm:@google/genai@1.34.0"

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
}

serve(async (req) => {
  if (req.method === "OPTIONS") {
    return new Response("ok", { headers: corsHeaders })
  }

  try {
    const supabaseUrl = Deno.env.get("SUPABASE_URL") ?? ""
    const supabaseAnonKey = Deno.env.get("SUPABASE_ANON_KEY") ?? ""
    const supabase = createClient(supabaseUrl, supabaseAnonKey)

    const authHeader = req.headers.get("Authorization")
    if (!authHeader) throw new Error("No authorization header")

    const { data: { user }, error: authError } = await supabase.auth.getUser(authHeader.replace("Bearer ", ""))
    if (authError || !user) throw new Error("Unauthorized")

    const { action, payload } = await req.json()
    const apiKey = Deno.env.get("GEMINI_API_KEY")
    if (!apiKey) throw new Error("GEMINI_API_KEY not found")

    const client = createGeminiClient({ apiKey })

    if (action === "chat") {
      const { messages, systemPrompt } = payload
      const history = messages.slice(0, -1).map((m: any) => ({
        role: m.role === "assistant" ? "model" : "user",
        parts: [{ text: m.content }]
      }))

      const chat = client.models.chat({
        model: "gemini-3-flash-preview",
        systemInstruction: systemPrompt,
        history: history
      })

      const result = await chat.sendMessage(messages[messages.length - 1].content)
      return new Response(JSON.stringify({ text: result.response.text() }), {
        headers: { ...corsHeaders, "Content-Type": "application/json" },
      })
    }

    if (action === "generate-story") {
      const { systemPrompt, userPrompt } = payload
      const result = await client.models.generateContent({
        model: "gemini-3-flash-preview",
        systemInstruction: systemPrompt,
        contents: [{ role: "user", parts: [{ text: userPrompt }] }],
        generationConfig: { responseMimeType: "application/json" }
      })
      return new Response(JSON.stringify({ text: result.response.text() }), {
        headers: { ...corsHeaders, "Content-Type": "application/json" },
      })
    }

    if (action === "generate-image") {
      const { prompts, model, base64Image, mimeType } = payload
      const targetModel = model || "gemini-2.5-flash-image"
      let parts = []
      if (base64Image) {
        parts.push({ inlineData: { data: base64Image.split(",")[1] || base64Image, mimeType: mimeType || "image/png" } })
      }
      if (Array.isArray(prompts)) prompts.forEach(p => parts.push({ text: p }))
      else parts.push({ text: prompts })

      const result = await client.models.generateContent({ model: targetModel, contents: [{ parts }] })
      
      let generatedImageBase64 = null
      let responseText = ""
      if (result.response.candidates?.[0]?.content?.parts) {
        for (const part of result.response.candidates[0].content.parts) {
          if (part.inlineData) generatedImageBase64 = part.inlineData.data
          else if (part.text) responseText = part.text
        }
      }

      return new Response(JSON.stringify({ text: responseText, image: generatedImageBase64 }), {
        headers: { ...corsHeaders, "Content-Type": "application/json" },
      })
    }

    throw new Error("Invalid action")
  } catch (error) {
    console.error("Error:", error)
    return new Response(JSON.stringify({ error: error.message }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 200,
    })
  }
})